import React, { useState, useEffect, useRef } from 'react';
import { Network } from 'vis-network';
import "./App.css";

function getEdgeColor(edges, newRouteEdge) {
  if (newRouteEdge[edges.from] && newRouteEdge[edges.from].includes(edges.to)) {
    return "#000000";
  } else {
    return "#FF0000";
  }
}

function GraphProcessor({weight, nodenames, path}) {
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const visJsRef = useRef(null);

  useEffect(() => {
    if (weight && nodenames) {
      const newNodes = [];
      const newEdges = [];
      for(let i = 0; i < weight.length; i++){
          newNodes.push({id: i, label: nodenames[i][0]});
          for(let j = 0; j < weight[i].length; j++){
              if(weight[i][j] !== 0) newEdges.push({from: i, to: j});
          }
      }
      setNodes(newNodes);
      setEdges(newEdges);
    }
  }, [weight, nodenames]);

  useEffect(() => {
    let newRouteEdge = [];
    if(path != null){
      for(let i = 0; i < path.length - 1; i++){
        if(newRouteEdge[path[i]] === undefined) {
          newRouteEdge[path[i]] = [path[i + 1]];
        } else {
          newRouteEdge[path[i]].push(path[i + 1])
        }
      }
    }
    // const getEdgeColor = (edge) => {
    //   if (newRouteEdge[edge.from] && newRouteEdge[edge.from].includes(edge.to)) ? "#000000" : "#FF0000";
    // };
    const network = new Network(
      visJsRef.current,
      { nodes, edges },
      {
        autoResize: true,
        edges: {
          color: "#000"
        }
      }
    );
    if (network.current) {
      const selectedEdgeId = 1; // the ID of the edge to be changed
      const selectedEdge = edges.get(selectedEdgeId);
      selectedEdge.color = 'blue';
      edges.update(selectedEdge);
    }
    return () => {
      network.destroy();
    };
  }, [visJsRef, nodes, edges, path]);

  return(<div ref={visJsRef} className="graph"/>);
}

export default GraphProcessor;
