import React, { useState, useEffect, useRef } from 'react';
import { Network } from 'vis-network';
import "./App.css";

function getEdgeColor(edges, newRouteEdge) {
  return newRouteEdge[edges.from] && newRouteEdge[edges.from].includes(edges.to) ? "#000000" : "#FF0000";
}

function GraphProcessor({ weight, nodenames, path }) {
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const visJsRef = useRef(null);

  useEffect(() => {
    if (weight && nodenames) {
      const newNodes = [];
      const newEdges = [];
      let id = 0;
      for (let i = 0; i < weight.length; i++) {
        newNodes.push({ id: `${i}`, label: nodenames[i][0] });
        for (let j = 0; j < weight[i].length; j++) {
          if (weight[i][j] !== 0) newEdges.push({ id: `${id}`, from: i, to: j });
          id++;
        }
      }
      setNodes(newNodes);
      setEdges(newEdges);
    }
  }, [weight, nodenames]);

  useEffect(() => {
    let routeEdge = [];
    if (path != null) {
      for (let i = 0; i < path.length - 1; i++) {
        if (routeEdge[path[i]] === undefined) {
          routeEdge[path[i]] = [path[i + 1]];
        } else {
          routeEdge[path[i]].push(path[i + 1])
        }
      }
    }
    const visJsRefCurrent = visJsRef.current;
    const network = new Network(
      visJsRefCurrent,
      { nodes, edges },
      {
        autoResize: true,
        edges: {
          color: {
            color: "black",
          }
        }
      }
    );
    console.log(network);
    for(let i = 0; i < network.body.data.edges.length; i++){
      if(routeEdge[network.body.data.edges[i].from].includes(network.body.data.edges[i].to)){
        network.body.data.edges.update({id: network.body.data.edges[i].id, color: {color: "red"}});
      }
    }
    return () => {
      network.destroy();
    };
  }, [nodes, edges, path]);

  return (<div ref={visJsRef} className="graph" />);
}

export default GraphProcessor;
