import React, { useState, useEffect, useRef } from 'react';
import { Network } from 'vis-network';

function GraphProcessor({weight, nodenames, path}) {
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  // console.log(weight);
  // console.log(nodenames);
  useEffect(() => {
    if (weight && nodenames) {
      // console.log("weight:");
      // console.log(weight);
      const newNodes = [];
      const newEdges = [];
      console.log(weight.length);
      for(let i = 0; i < weight.length; i++){
          newNodes.push({id: i, label: nodenames[i][0]});
          // console.log("newNodes");
          for(let j = 0; j < weight[i].length; j++){
              if(weight[i][j] !== 0) newEdges.push({from: i, to: j});
              // console.log(newEdges);
          }
      }
      setNodes(newNodes);
      setEdges(newEdges);
    }
  }, [weight, nodenames]);

  const visJsRef = useRef(null);
  // console.log(nodes);
  // console.log(edges);
  useEffect(() => {
      visJsRef.current &&
      new Network(
        visJsRef.current,
        { nodes, edges },
        {
          // autoResize: true,
          edges: {
            color: '#999999', // Default color for all edges
            chosen: {
              edge: (values, id, selected, hovering) => {
                values.color = selected ? '#FF0000' : '#999999'; // Change color for selected edges
              }
            },
            // Set custom color for specific edges based on array of edge IDs
            color: {
              highlight: '#FF0000', // Highlight color
              hover: '#CCCCCC', // Hover color
              opacity: 1.0, // Opacity
              inherit: false, // Don't inherit default color
              color: edge => {
                return selectedEdgeIds.includes(edge.id) ? '#FF0000' : '#999999';
              }
