import React, { useState, useEffect, useRef } from 'react';
import { Network } from 'vis-network';
import "./App.css";

function getEdgeColor(edges, newRouteEdge) {
  return newRouteEdge[edges.from] && newRouteEdge[edges.from].includes(edges.to) ? "#000000" : "#FF0000";
}

function GraphProcessor({ weight, nodenames, path }) {
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const visJsRef = useRef(null);

  useEffect(() => {
    let routeEdge = [];
    if (path != null) {
      // initialize routeEdge as an array of arrays
      routeEdge = Array.from({ length: weight.length }, () => []);
      for (let i = 0; i < path.length - 1; i++) {
        routeEdge[path[i]][path[i + 1]] = true;
      }
    }
    if (weight && nodenames) {
      const newNodes = [];
      const newEdges = [];
      let id = 0;
      for (let i = 0; i < weight.length; i++) {
        newNodes.push({ id: `${i}`, label: nodenames[i][0]});
        for (let j = 0; j < weight[i].length; j++) {
          if (weight[i][j] !== 0){
            if(routeEdge[i][j] === true){
              newEdges.push({ id: `${id}` + 'isRoute', from: i, to: j , label: weight[i][j]});
            }
            else newEdges.push({ id: `${id}`, from: i, to: j , label: weight[i][j]});
          }
          id++;
        }
      }
      setNodes(newNodes);
      setEdges(newEdges);
    }
  }, [weight, nodenames]);

  useEffect(() => {
    const visJsRefCurrent = visJsRef.current;
    const network = new Network(
      visJsRefCurrent,
      { nodes, edges },
      {
        autoResize: true,
        edges: {
          color: {
            color: "black",
          }
        }
      }
    );
    console.log(network);
    // for(let i = 0; i < network.body.data.edges.length; i++){
    //     try{network.body.data.edges.update({id: `${i}` + 'isRoute', color: {color: "red"}})} catch(err){};
    // }
    return () => {
      network.destroy();
    };
  }, [nodes, edges, path]);

  return (<div ref={visJsRef} className="graph" />);
}

export default GraphProcessor;
